insert into agricultural_diagnosis.treatment (id, createdAt, updatedAt, type, method, recommendedProducts, diseaseId, precautions)
values  (1, '2025-04-01 00:43:24.781000', '2025-04-01 00:43:24.781000', 'biological', '选用抗病品种', null, 1, null),
        (2, '2025-04-01 00:43:24.782000', '2025-04-01 00:43:24.782000', 'cultural', '合理施肥，避免过量施用氮肥', null, 1, null),
        (3, '2025-04-01 00:43:24.783000', '2025-04-01 00:43:24.783000', 'chemical', '发病初期使用化学药剂', '三环唑', 1, null),
        (6, '2025-04-17 21:14:09.565000', '2025-04-17 21:14:09.565000', 'cultural', '清理果园时注意将剪除枝、清扫病落叶、收集带病僵果等集中烧毁；加强栽培管理，增强树势，提高抗病能力[ty-reference](7)。', null, 3, null),
        (7, '2025-04-17 21:14:09.566000', '2025-04-17 21:14:09.566000', 'chemical', '从萌芽期开始喷施药剂如40%克菌丹可湿性粉剂450-500倍液，每隔10-14天喷1次，连续2-3次[ty-reference](7)。', '40%克菌丹可湿性粉剂', 3, null),
        (8, '2025-04-25 22:57:52.934926', '2025-04-25 22:57:52.934926', 'chemical', '在发病初期喷洒药剂，如64%恶霜锰锌可湿性粉剂500倍液，或72%霜脲·锰锌可湿性粉剂600倍液，每隔7-10天喷一次，连续2-3次。', '64%恶霜锰锌可湿性粉剂, 72%霜脲·锰锌可湿性粉剂', 8, null),
        (9, '2025-04-25 22:57:52.936241', '2025-04-25 22:57:52.936241', 'biological', '使用生物制剂如枯草芽孢杆菌（Bacillus subtilis）或木霉菌（Trichoderma spp.）进行喷施，抑制病原菌生长。', '枯草芽孢杆菌制剂, 木霉菌制剂', 8, null),
        (10, '2025-04-25 22:57:52.937110', '2025-04-25 22:57:52.937110', 'physical', '清除病叶、病果和病株残体，并集中烧毁；避免田间积水，改善通风透光条件。', '', 8, null),
        (11, '2025-04-25 22:57:52.937973', '2025-04-25 22:57:52.937973', 'cultural', '选用抗病品种；合理轮作，避免连作；控制种植密度，保持田间卫生；及时排水降低田间湿度。', '', 8, null),
        (12, '2025-04-25 22:59:11.140770', '2025-04-25 22:59:11.140770', 'chemical', '喷洒杀虫剂防治柑橘木虱，如25%噻虫嗪水分散粒剂3000倍液，或10%吡虫啉可湿性粉剂1500倍液。', '25%噻虫嗪水分散粒剂, 10%吡虫啉可湿性粉剂', 9, null),
        (13, '2025-04-25 22:59:11.141777', '2025-04-25 22:59:11.141777', 'biological', '利用天敌如瓢虫和寄生蜂控制柑橘木虱的数量；使用微生物制剂如苏云金芽孢杆菌（Bacillus thuringiensis）进行生物防治。', '苏云金芽孢杆菌制剂', 9, null),
        (14, '2025-04-25 22:59:11.143072', '2025-04-25 22:59:11.143072', 'physical', '及时清除病株并销毁，防止病害扩散；安装防虫网减少木虱侵害。', '', 9, null),
        (15, '2025-04-25 22:59:11.143966', '2025-04-25 22:59:11.143966', 'cultural', '选用无病苗木种植；加强田间管理，合理修剪，保持通风透光；避免在病区调运苗木和接穗。', '', 9, null),
        (16, '2025-04-25 23:02:27.937000', '2025-04-25 23:02:27.937000', 'chemical', '在春季发病初期喷施杀菌剂如25%三唑酮可湿性粉剂1000倍液，每隔10-14天喷一次，连续2-3次。', '25%三唑酮可湿性粉剂', 10, null),
        (17, '2025-04-25 23:02:27.938000', '2025-04-25 23:02:27.938000', 'biological', '利用抗病品种减少病害发生；目前生物防治方法对苹果锈叶病的效果有限。', '', 10, null),
        (18, '2025-04-25 23:02:27.940000', '2025-04-25 23:02:27.940000', 'physical', '清除果园周边的桧柏等转主寄主植物，减少病原菌来源。', '', 10, null),
        (19, '2025-04-25 23:02:27.941000', '2025-04-25 23:02:27.941000', 'cultural', '加强果园管理，合理修剪，改善通风透光条件，降低湿度；避免与桧柏类植物相邻种植。', '', 10, null),
        (20, '2025-04-25 23:04:51.651000', '2025-04-25 23:04:51.651000', 'chemical', '发病初期使用铜制剂如波尔多液或77%氢氧化铜可湿性粉剂500倍液喷雾防治，每隔7-10天一次，连续2-3次。', '波尔多液, 77%氢氧化铜可湿性粉剂', 11, null),
        (21, '2025-04-25 23:04:51.653000', '2025-04-25 23:04:51.653000', 'biological', '应用生物控制剂如荧光假单胞菌（Pseudomonas fluorescens）等有益微生物抑制病原菌生长。', '荧光假单胞菌制剂', 11, null),
        (22, '2025-04-25 23:04:51.655000', '2025-04-25 23:04:51.655000', 'physical', '及时清除并销毁病残体，减少田间病原基数；避免过度浇水和田间积水。', '', 11, null),
        (23, '2025-04-25 23:04:51.657000', '2025-04-25 23:04:51.657000', 'cultural', '轮作换茬，避免与茄科作物连作；选择抗病品种；合理密植，保持田间通风透光。', '', 11, null),
        (24, '2025-04-28 13:13:01.323000', '2025-04-28 13:13:01.323000', 'chemical', '在发病初期使用苯醚甲环唑等药剂进行喷雾防治，每隔7-10天喷洒一次，连续2-3次。', '苯醚甲环唑', 12, null),
        (25, '2025-04-28 13:13:01.327000', '2025-04-28 13:13:01.327000', 'biological', '使用生物农药如枯草芽孢杆菌（Bacillus subtilis）进行防治，能够抑制炭疽病的传播。', '枯草芽孢杆菌制剂', 12, null),
        (26, '2025-04-28 13:13:01.328000', '2025-04-28 13:13:01.328000', 'physical', '及时清除病残体，并进行深埋或销毁，减少病原菌的存活基数。', '', 12, null),
        (27, '2025-04-28 13:13:01.329000', '2025-04-28 13:13:01.329000', 'cultural', '进行合理轮作，避免与豆科作物连作；加强田间管理，避免过度密植，保持田间通风透光。', '', 12, null),
        (28, '2025-04-28 13:15:24.304000', '2025-04-28 13:15:24.304000', 'chemical', '在发病初期使用三唑类药物如氯噻吨、丙环唑等进行喷洒，每隔7-10天喷洒一次，连续2-3次。', '氯噻吨, 丙环唑', 13, null),
        (29, '2025-04-28 13:15:24.306000', '2025-04-28 13:15:24.306000', 'biological', '应用生物防治剂如赤霉病菌（Trichoderma spp.）和枯草芽孢杆菌（Bacillus subtilis），帮助抑制锈病菌的传播。', '枯草芽孢杆菌制剂', 13, null),
        (30, '2025-04-28 13:15:24.307000', '2025-04-28 13:15:24.307000', 'physical', '及时清除病残体和叶片，减少病原基数；定期进行田间管理，保持田间干燥和良好的空气流通。', '', 13, null),
        (31, '2025-04-28 13:15:24.309000', '2025-04-28 13:15:24.309000', 'cultural', '实行轮作制度，避免与大豆连作；合理密植，保持适当的株距，以促进通风透光，减少病害发生。', '', 13, null),
        (32, '2025-04-28 13:21:37.983000', '2025-04-28 13:21:37.983000', 'chemical', '使用多菌灵、苯醚甲环唑等化学药剂进行防治。发病初期喷洒，防治期间每隔7-10天喷一次，确保覆盖均匀。', '多菌灵, 苯醚甲环唑', 14, null),
        (33, '2025-04-28 13:21:37.984000', '2025-04-28 13:21:37.984000', 'biological', '应用生物防治剂如枯草芽孢杆菌（Bacillus subtilis）或其他有益微生物抑制病菌生长。', '枯草芽孢杆菌制剂', 14, null),
        (34, '2025-04-28 13:21:37.984000', '2025-04-28 13:21:37.984000', 'physical', '及时清除病残体并销毁，避免病菌在田间滋生；合理修剪枝条，保持树木通风透光，减少湿气。', '', 14, null),
        (35, '2025-04-28 13:21:37.985000', '2025-04-28 13:21:37.985000', 'cultural', '实施轮作，避免与苹果同科作物连作；合理施肥，增强树木抵抗力；加强田间管理，保持干燥环境。', '', 14, null);